mapper.select:
select
id, categ_id, apply_order_no, app_id, app_name, platform, platform_order_no, platform_app_id, platform_app_name,
applicant, tenant_name, tenant_id, deleted
from app_apply


        and id = #{id}


        and categ_id = #{categId}


        and apply_order_no = #{applyOrderNo}


        and app_id = #{appId}


        and app_name = #{appName}


        and platform = #{platform}


        and platform_order_no = #{platformOrderNo}


        and platform_app_id = #{platformAppId}


        and platform_app_name = #{platformAppName}


        and applicant = #{applicant}


        and tenant_name = #{tenantName}


        and tenant_id = #{tenantId}


        and deleted = #{deleted}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from app_apply


        and id = #{id}


        and categ_id = #{categId}


        and apply_order_no = #{applyOrderNo}


        and app_id = #{appId}


        and app_name = #{appName}


        and platform = #{platform}


        and platform_order_no = #{platformOrderNo}


        and platform_app_id = #{platformAppId}


        and platform_app_name = #{platformAppName}


        and applicant = #{applicant}


        and tenant_name = #{tenantName}


        and tenant_id = #{tenantId}


        and deleted = #{deleted}
