mapper.select:
select
id, app_id, app_name, apply_order_no, platform_order_no, platform_account, platform_app_name, cert_no,
legal_name, cert_name, contact_phone, contact_name, deleted
from app_apply_item


        and id = #{id}


        and app_id = #{appId}


        and app_name = #{appName}


        and apply_order_no = #{applyOrderNo}


        and platform_order_no = #{platformOrderNo}


        and platform_account = #{platformAccount}


        and platform_app_name = #{platformAppName}


        and cert_no = #{certNo}


        and legal_name = #{legalName}


        and cert_name = #{certName}


        and contact_phone = #{contactPhone}


        and contact_name = #{contactName}


        and deleted = #{deleted}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from app_apply_item


        and id = #{id}


        and app_id = #{appId}


        and app_name = #{appName}


        and apply_order_no = #{applyOrderNo}


        and platform_order_no = #{platformOrderNo}


        and platform_account = #{platformAccount}


        and platform_app_name = #{platformAppName}


        and cert_no = #{certNo}


        and legal_name = #{legalName}


        and cert_name = #{certName}


        and contact_phone = #{contactPhone}


        and contact_name = #{contactName}


        and deleted = #{deleted}
